import styled from "styled-components";

export const Section = styled.section`
width:80%;
margin:0 auto;
margin-top:60px;
margin-bottom:40px;
border:1px solid white;
`;
export const Title = styled.div`
background-color:white;
text-align  :center;
padding:15px;
& h2{
  color: #000;
  text-transform:uppercase;
  font-size:36px;
  margin:0;
}
`;
export const Main = styled.div`
  padding: 30px;
  align-items: center;
  display: flex;
  flex-wrap:wrap;
  justify-content:center;
  @media (min-width: 992px) {
  justify-content:unset;

  }
`;

export const Image = styled.div`
  width: 100%;
  & figure {
    width: 150px;
    height: 150px;
    margin:0 auto;
    margin-bottom:50px;
  }
  & img {
    height: 100%;
    object-fit: contain;
  }
  @media (min-width: 992px) {
  width: 25%;
    & figure {
      width: 200px;
      height: 200px;
    }
  }
`;

export const Info = styled.div`
  width: auto;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding-left: 15px;
  & h3 {
    font-size: 24px;
    text-transform: uppercase;
    margin: 0;
    padding-bottom: 20px;
  }
  & p {
    font-size: 14px;
    margin: 0;
    padding-right: 60px;
    box-sizing: content-box;
  }
  @media (min-width: 992px) {
  border-right: 1px solid white;
    width:45%
  }
`;
export const Bullets = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding-top:30px;
  & p {
    font-size: 14px;
    margin: 0;
    padding-left: 40px;
    padding-bottom: 10px;
    text-transform: capitalize;
  }
  @media (min-width: 992px) {
  width: 25%;
  padding-top:0px;

  }
`;
