import styled from "styled-components";

export const Section = styled.article`
  width: 48%;
  margin-bottom: 40px;
  border: 1px solid white;
  text-align:center;

`;
export const Title = styled.article`
  background-color:white;
  color:black;
  padding:30px 0;
  font-size:36px;
  text-transform:uppercase;
  font-weight:600;
`;
export const Figure = styled.article`
  max-width:228px;
  max-height:218px;
  height:100%;
  margin:0 auto;
  & img{
    height:100%;
    object-fit:cover;
  }
`;
export const Name = styled.article`
  font-size:24px;
  margin-top:30px;
  margin-bottom:30px;
  text-transform:uppercase;
  padding:0 20px;
`;
export const Text = styled.article`
padding:0 20px;
text-align:justify;
padding-bottom:30px;

`;
export const Info = styled.article`
  padding:0 20px;
  display:flex;
  justify-content:space-between;
`;
export const Bullet = styled.article`
padding:0 20px;
  & p{
    margin:0;
    text-align:left;
  }
`;
