import React,{useState, useEffect} from "react";
import getPastLaunches from "../../services/getPastLaunchesNP";

import StatCard from "../StatCard";

import { Grid } from "./styles";

const GridStats = () => {


  const dataStarlink = {
    labels: ["Red", "Blue", "Yellow", "Green"],
    datasets: [
      {
        label: "# of Votes",
        data: [12, 19, 3, 5],
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  const [dragonInfo, setdragonInfo] = useState([])
  const [succes, setsucces] = useState('')
  const [fail, setfail] = useState('')


    useEffect(() => {
      getPastLaunches().then((data) => {
        setdragonInfo(data);
      });
    }, []);
   console.log(fail);

   useEffect(()=>{
     const succeslaunch = dragonInfo.filter(item=>{
      return !item.success 
     })
     const faillaunch = dragonInfo.filter(item=>{
      return item.success 
     })
     setsucces(succeslaunch.length)
     setfail(faillaunch.length)
   },[dragonInfo])


   const dataSucces = {
     labels: ["crs", "crew"],
     datasets: [
       {
         label: "# of Votes",
         data: [4, 23],
         backgroundColor: [
           "rgba(255, 99, 132, 0.2)",
           "rgba(54, 162, 235, 0.2)",
         ],
         borderColor: ["rgba(255, 99, 132, 1)", "rgba(54, 162, 235, 1)"],
         borderWidth: 1,
       },
     ],
   };

  return (
    <Grid>
      <StatCard title="launch stats" items={dataSucces} />
      <StatCard title="Starlink- in space" items={dataStarlink} />
    </Grid>
  );
};

export default GridStats;
