import React,{useState, useEffect} from 'react';
import { Section, Counter, Number, NumberContainer, Info } from "./styles";
import getNextLaunch from "../../services/getNextLaunch";
import getOneRocket from "../../services/getOneRocket";


const CountDown = () => {
  const [nextlaunch, setNextlaunch] = useState({});
  const [rocketUsed, setRocketUsed] = useState({});


  useEffect(() => {
    getNextLaunch()
    .then((data) =>{ 
      setNextlaunch({...data})
      if (nextlaunch.rocket) {
        return getOneRocket(nextlaunch.rocket).then((data) =>
          setRocketUsed({ ...data })
        )
      }
    })

  }, []);

  const getRemainingTime = (deadline) => {
    let now = new Date(),
      remainTime = (new Date(deadline) - now + 1000) / 1000,
      remainSeconds = ("0" + Math.floor(remainTime % 60)).slice(-2),
      remainMinutes = ("0" + Math.floor((remainTime / 60) % 60)).slice(-2),
      remainHours = ("0" + Math.floor((remainTime / 3600) % 24)).slice(-2),
      remainDays = Math.floor(remainTime / (3600 * 24));

    return {
      remainSeconds,
      remainMinutes,
      remainHours,
      remainDays,
      remainTime,
    };
  };

  const countdown = (deadline,  finalMessage) => {

    const timerUpdate = setInterval(() => {
      let t = getRemainingTime(deadline);
      const r = (
        <p>{

          `${t.remainDays}d:${t.remainHours}h:${t.remainMinutes}m:$
          {t.remainSeconds}s`
        }
        </p>
      );
      // console.log(r);
      return r
      //   clearInterval(timerUpdate);
      // if (t.remainTime <= 1) {
        //   el.innerHTML = finalMessage;
        // }

      }, 1000);
      console.log(timerUpdate )
  };
  console.log(countdown("Dec 31 2025 21:34:40 GMT-0500", "¡Ya empezó!"));
  return (
    <Section>
      <Counter>
        <h1>Up Next</h1>

        <NumberContainer>
          <Number>
            <p>3</p>
            <small>days
            {countdown('Dec 31 2025 21:34:40 GMT-0500',  '¡Ya empezó!')}
            </small>
          </Number>
          <Number>
            <p>3</p>
            <small>days</small>
          </Number>
          <Number>
            <p>3</p>
            <small>days</small>
          </Number>
          <Number>
            <p>3</p>
            <small>days</small>
          </Number>
        </NumberContainer>
        <Info>
          <p>
            <span>mission:</span> {nextlaunch.name}
          </p>
          <p>
            <span>Rocket:</span> {rocketUsed.name}
          </p>
        </Info>
      </Counter>
    </Section>
  );
};

export default CountDown;
