import React,{useState, useEffect} from "react";
import getPastLaunches from "../../services/getPastLaunchesNP";
import getPayload from "../../services/getPayload";

import StatCard from "../StatCard";

import { Grid } from "./styles";

const GridStats = () => {


  const [dragonInfo, setdragonInfo] = useState([]);
  const [succes, setsucces] = useState("");
  const [fail, setfail] = useState("");

  const [customer, setcustomer] = useState([]);
  const [customerName, setcustomerName] = useState([]);


  // bring the data for launch stats
  useEffect(() => {
    getPastLaunches().then((data) => {
      setdragonInfo(data);
    });
  }, []);
  useEffect(() => {
    const succeslaunch = dragonInfo.filter((item) => {
      return !item.success;
    });
    const faillaunch = dragonInfo.filter((item) => {
      return item.success;
    });
    setsucces(succeslaunch.length);
    setfail(faillaunch.length);
  }, [dragonInfo]);

    const dataSucces = {
      labels: ["fail", "succes"],
      datasets: [
        {
          label: "# of Votes",
          data: [fail, succes],
          backgroundColor: ["rgba(255, 99, 132, 0.2)", "rgba(54, 162, 235, 0.2)"],
          borderColor: ["rgba(255, 99, 132, 1)", "rgba(54, 162, 235, 1)"],
          borderWidth: 1,
        },
      ],
    };

  // bring the data for customer stats
  useEffect(() => {
    getPayload().then((data) => {
      setcustomer(data);
    });
  }, []);
  console.log(customer)

  useEffect(()=>{
    const name= customer.map((item)=>{
      console.log(item.customer[0]);
    })
    // setcustomerName(name)
  },[customer])

  const dataCustomer = {
    labels: [],
    datasets: [
      {
        label: "# of Votes",
        data: [12, 19, 3, 5],
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <Grid>
      <StatCard title="launch stats" items={dataSucces} />
      <StatCard title="commercial Customers" items={dataCustomer} />
    </Grid>
  );
};

export default GridStats;
